// Project Euler Problem 8
// Find the greatest product of 5 consective digits in the 1000 digit number...

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace ProjectEuler
{
    class EulerEight
    {
        static string number = "";
        static void Main(string[] args)
        {
            ulong product, oldProduct = 0;
            ulong[] num = new ulong[5];
            for (int i = 0; i < 995; i++)
            {
                num[0] = Convert.ToUInt64(number.Substring(i, 1));
                num[1] = Convert.ToUInt64(number.Substring(i + 1, 1));
                num[2] = Convert.ToUInt64(number.Substring(i + 2, 1));
                num[3] = Convert.ToUInt64(number.Substring(i + 3, 1));
                num[4] = Convert.ToUInt64(number.Substring(i + 4, 1));
                product = num[0] * num[1] * num[2] * num[3] * num[4];
                if (product > oldProduct)
                    oldProduct = product;
            }
            Console.Write(oldProduct);
            Console.ReadLine();
        }
    }
}
